definition.script:
pipeline {
    agent any

    stages {
        stage('Build Catalog tar.gz') {
            steps {
                sh "export CATALOG_NAME=${env.CATALOG_NAME}; create-catalog.sh;"     
            }
        }
        stage('Mirroring'){
            steps {
                sh "curl -X POST \"http://operator-catalog-utils:8080/applyImageMirrors\" -H \"accept: */*\" -H \"Content-Type: multipart/form-data\" -F \"json-mirrors-file=@/var/jenkins_home/mirroring/mirrors.json\" -F \"package-manifest-file=@${env.CATALOG_NAME}.tar.gz\" > ${env.CATALOG_NAME}-mirrored.tar.gz; ls -lht; mv ${env.CATALOG_NAME}-mirrored.tar.gz ${env.CATALOG_NAME}.tar.gz "
                sh "cp ${env.CATALOG_NAME}.tar.gz ${env.JENKINS_HOME}/operatorhub/"
            }
        }
        stage('Post build action'){
            steps {
               sh "${env.POST_CATALOG_CREATION_SCRIPT}" 

            }
        }
    }
}
